<?php include('./header.php'); ?><h1>ブログ記事の投稿</h1><?phpif (isset($_POST["title"]) && isset($_POST["contents"])) {	if(!isset($_POST["password"])||$_POST["password"] !== "abc"){		echo "<p>パスワードが違います<p>";	}else{		$title = $_POST["title"];		$contents = $_POST["contents"];		try{			//タイムゾーンの指定			ini_set("date.timezone", "Asia/Tokyo");			//$timeへ成形した年月日および時刻データを格納			$time = date("Y.m.d-H:i");			//PDOクラスのオブジェクトの作成			$dbh = new PDO("sqlite:blog.db"," "," ");			//実行するSQL文を$sqlに格納			$sql = 'insert into posts(title,contents,date) values(?, ?, ?)';			//prepareメソッドでSQL文の準備			$sth = $dbh->prepare($sql);			//prepareした$sthを実行　SQL文の？部に格納する変数を指定			$sth->execute(array($title, $contents, $time));			if ($sth) {				echo "記事１件を投稿しました";			} else {				echo "記事１件の投稿に失敗しました";			}		} Catch (PDOException $e) {			print "エラー!: " . $e->getMessage() . "<br/>";			die();		}	}}$dbh = null;?><form action="<?php echo $_SERVER['PHP_SELF'] ?>" method="post">	<ul>		<li>			<label>表題</label>			<input type="text" name="title" size="60" />		</li>		<li>			<label>本文</label>			<textarea name="contents" rows="10" cols="60"></textarea>		</li>		<li>			<label>パスワード</label>			<input type="password" name="password" size="20">		</li>		<li>			<input type="reset" value="リセット"  class="sbt" />			<input type="submit" value="投稿" class="sbt" />		</li>	</ul></form><?php include('./footer.php'); ?>